mapper.insert:
INSERT INTO attendance (s_id, the_time, as_no)

	
		values(#{sId}, current_timestamp, if( 
			(select min(t.tt_starttime) from course_register cr 
			inner join timetable t on cr.reg_c_no = t.c_no
			where t.tt_day = 1 and cr.reg_s_id=#{sId}
			) > hour(current_time)
		,2,3))	
	
	
		values (#{sId}, current_timestamp, if( 
			(select max(t.tt_endtime) from course_register cr 
			inner join timetable t on cr.reg_c_no = t.c_no
			where t.tt_day = 1 and cr.reg_s_id=#{sId}
			) > hour(current_time)
		,4,5))

if.if:
select if( 
	(select min(t.tt_starttime) from course_register cr 
	inner join timetable t on cr.reg_c_no = t.c_no
	where t.tt_day = 1 and cr.reg_s_id=#{sId}
	) > hour(current_time),2,3)

select if( 
	(select max(t.tt_endtime) from course_register cr 
	inner join timetable t on cr.reg_c_no = t.c_no
	where t.tt_day = 1 and cr.reg_s_id=#{sId}
	) > hour(current_time),4,5)

mapper.update:
UPDATE attendance

	
		s_id=#{student.sId},
	
	
		the_time= current_timestamp,
	
	
		as_no= #{attendanceStatus.asNo}
				

	WHERE at_no = #{atNo}

UPDATE attendance

	
		s_id=#{student.sId},
	
	
		the_time= #{theTime},
	
	
		as_no= #{attendanceStatus.asNo}
				

	WHERE at_no = #{atNo}

mapper.select:
select s.s_id, s.s_name, s.s_phone, s.school, att.the_time, att.at_no, ass.as_no, ass.as_status from course_register cr
inner join timetable tt on tt.c_no = cr.reg_c_no
inner join student s on s.s_id = cr.reg_s_id
left outer join attendance att on cr.reg_s_id = att.s_id and date(att.the_time) = current_date
left outer join attendance_status ass on att.as_no = ass.as_no

	
		tt.tt_day = #{ttDay} 
	
	
		and cr.reg_c_no = #{cNo} 
	
	
		and cr.reg_month=#{regMonth} 
	

group by s.s_id, att.the_time

select att.*, ass.as_status from attendance att
inner join attendance_status ass on att.as_no = ass.as_no
 WHERE att.at_no = #{atNo}

SELECT at_no, s_id, the_time, att.as_no, ats.as_status FROM attendance att 
inner join attendance_status ats on ats.as_no = att.as_no
where att.the_time > #{theTime} 
and att.s_id = #{student.sId} 
and at_no = (select max(att2.at_no) from attendance att2 where att2.s_id= #{student.sId})
